    console.log(account)
  }

  useEffect(() => {
    loadBlockchainData()
  }, [])

  return (
    <div>
      <Navigation account={account} setAccount={setAccount} />
      <h1>{account}</h1>

      <main>

      </main>
    </div>
  );
}

export default App;
